}
return [ msg ];

ha_external_url  = "https://hass.microserver.space";
frigate_external_url = "https://frigate.microserver.space"

msg.event_id     = msg.payload.after.id;
msg.clip         = ha_external_url + "/api/frigate/notifications/" + msg.event_id + "/" + msg.payload.after.camera + "/clip.mp4";
msg.thumbnail    = ha_external_url + "/api/frigate/notifications/" + msg.event_id + "/thumbnail.jpg?format=android";
msg.topic        = msg.payload.after.camera + "-" + msg.payload.after.label;
msg.relativetime = msg.relativetime || "7 minutes";

switch (msg.payload.after.camera) {
    case('office'):
        msg.verb = "at";
        msg.location = 'office';
        msg.uri = frigate_external_url + "/api/office?h=1080";
        break;
    case('driveway'):
        msg.verb = "in";
        msg.location = "driveway";
        msg.uri = frigate_external_url + "/api/garage?h=1080";
        break;    
    case('lounge'):
        msg.verb = "in";
        msg.location = "lounge";
        msg.uri = frigate_external_url + "/api/garage?h=1080";
        break;

    default:
        msg.verb = "in";
        msg.location = msg.payload.after.camera;
}

msg.message     = "A " + msg.payload.after.label + " is " + msg.verb + " the " + msg.location;

msg.payload     = { data: {'message': msg.message, data: {'image': msg.thumbnail, 'clickAction': msg.clip, 'sticky': true,'color': "#2DF56D", 'actions': [{'action': "URI",'title': "Watch Live Stream", 'uri': msg.uri  }],'ttl': 0, 'group': "motion-group", 'priority': "High"  }}};
return msg;

// Defaults to 10 minutes, overridable with msg.ratelimitms
// Will limit per topic (including by null ones)
var interval = msg.ratelimitms || (1000*60*10); // minimum interval between messages (ms)
var countToTrigger = msg.countToTrigger || 1;
var lastReleasedKey = "lastReleased_" + (msg.topic.replace(/ /g, "_") || "null_topic");
var lastHitKey = "lastHit_" + (msg.topic.replace(/ /g, "_") || "null_topic");
var cntKey = "count_" + (msg.topic.replace(/ /g, "_") || "null_topic");
var lastReleased = (context.get(lastReleasedKey) || 0);
var lastHit = (context.get(lastHitKey) || 0);
var now = Date.now();
if (now-lastHit > interval) {
    node.warn("resetting count, too far apart.");
    context.set(cntKey,0); // Clear hits, too far apart.
}
context.set(lastHitKey,now);
if (now-lastReleased > interval) {
    var count = (context.get(cntKey) || 0);
    count++;
    if (count >= countToTrigger) {
        context.set(lastReleasedKey,now);
        context.set(cntKey,0);
        node.status({fill:"green",shape:"ring",text:"passed on"});
        return msg;
    } else {
        node.status({fill:"red",shape:"ring",text:"Waiting for " + 
            (countToTrigger - count) + " more hits."});
        context.set(cntKey,count);
        return null;
    }
} else {
    var timeLeft = (interval - (now-lastReleased)) / 1000;  
    node.status({fill:"red",shape:"ring",text:"blocked for " + timeLeft + " seconds"});
    return null;
}
